import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import { useSocket } from '../context/SocketContext';

const HomePage = () => {
  const [playerName, setPlayerName] = useState('');
  const [lobbyCode, setLobbyCode] = useState('');
  const [showJoinForm, setShowJoinForm] = useState(false);
  const { createLobby, isLoading, error } = useGame();
  const { connected } = useSocket();
  const navigate = useNavigate();

  const handleCreateLobby = async () => {
    if (!playerName.trim()) {
      alert('Please enter your name');
      return;
    }

    const lobbyId = await createLobby();
    if (lobbyId) {
      navigate(`/lobby/${lobbyId}?name=${encodeURIComponent(playerName)}`);
    }
  };

  const handleJoinLobby = () => {
    if (!playerName.trim()) {
      alert('Please enter your name');
      return;
    }
    if (!lobbyCode.trim()) {
      alert('Please enter a lobby code');
      return;
    }

    navigate(`/lobby/${lobbyCode.toUpperCase()}?name=${encodeURIComponent(playerName)}`);
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4">
      <div className="bg-white rounded-xl shadow-2xl p-8 w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            Monopoly Deal
          </h1>
          <p className="text-gray-600">
            Online Multiplayer Card Game
          </p>
          <div className="mt-4">
            <span className={`inline-block px-3 py-1 rounded-full text-sm ${
              connected 
                ? 'bg-green-100 text-green-800' 
                : 'bg-red-100 text-red-800'
            }`}>
              {connected ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}
            </span>
          </div>
        </div>

        {/* Error Display */}
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        {/* Player Name Input */}
        <div className="mb-6">
          <label htmlFor="playerName" className="block text-sm font-medium text-gray-700 mb-2">
            Your Name
          </label>
          <input
            type="text"
            id="playerName"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
            placeholder="Enter your name"
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-monopoly-blue focus:border-transparent"
            maxLength={20}
          />
        </div>

        {/* Main Actions */}
        <div className="space-y-4">
          <button
            onClick={handleCreateLobby}
            disabled={isLoading || !connected}
            className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isLoading ? 'Creating...' : 'Create New Game'}
          </button>

          <div className="text-center text-gray-500">
            or
          </div>

          {!showJoinForm ? (
            <button
              onClick={() => setShowJoinForm(true)}
              disabled={!connected}
              className="w-full btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Join Existing Game
            </button>
          ) : (
            <div className="space-y-3">
              <input
                type="text"
                value={lobbyCode}
                onChange={(e) => setLobbyCode(e.target.value.toUpperCase())}
                placeholder="Enter lobby code"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-monopoly-blue focus:border-transparent"
                maxLength={8}
              />
              <div className="flex space-x-2">
                <button
                  onClick={handleJoinLobby}
                  disabled={isLoading || !connected}
                  className="flex-1 btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Join Game
                </button>
                <button
                  onClick={() => {
                    setShowJoinForm(false);
                    setLobbyCode('');
                  }}
                  className="flex-1 btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Instructions */}
        <div className="mt-8 text-center text-sm text-gray-600">
          <h3 className="font-semibold mb-2">How to Play:</h3>
          <ul className="text-left space-y-1">
            <li>â€¢ Collect 3 complete property sets to win</li>
            <li>â€¢ Play up to 3 cards per turn</li>
            <li>â€¢ Use action cards to steal and charge rent</li>
            <li>â€¢ Support for 2-5 players</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
